#!/bin/bash
set -e
set -x
export ARTIFACTORY_USERNAME=$7
export ARTIFACTORY_APIKEY=$8
#"kit-cloud-app" "quickstart-kit-cloud-app.sh" "${env.MORE_SWITCHES}" "${env.INSTANCE_PREPENDER}" "${env.BRANCH_NAME}" "mvn_settings_snapshot.xml"
echo "Artifactory user  is: $ARTIFACTORY_USERNAME"
echo "Api Key is: $ARTIFACTORY_APIKEY"

if [ -d "quickstart" ]; then
  rm -rf quickstart
fi
workspace=`pwd`
mkdir -p predix
chmod 777 predix
cd predix

#curl -O https://github.build.ge.com/raw/adoption/kit-cloud-app/$3/scripts/quickstart-kit-cloud-app.sh
#chmod 755 quickstart-kit-cloud-app.sh
#./quickstart-kit-cloud-app.sh $1 --skip-setup -i $2 -b $3 -s $4

curl -O https://github.build.ge.com/raw/adoption/$1/$5/scripts/$2;
chmod 755 $2
./$2 $3 --skip-setup -i $4 -b $5 -s $workspace/library/scripts/$6
