#!/bin/bash

if [[ $2 == *"ge.com"* ]]; then
  api_url="https://github.build.ge.com/api/v3/repos/adoption/$1/tags"
else
  api_url="https://api.github.com/repos/PredixDev/$1/tags"
fi
tag=$(curl -s -H "Cache-Control: no-cache" -L $api_url | jq -r '.[0]?.name?')
if [[ $tag != "null" ]]; then
  echo $tag
else
  echo "master"
fi
